-- ----------------------------
-- Table structure for `article`
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) COLLATE utf8_esperanto_ci NOT NULL COMMENT '标题',
  `classId` int(10) NOT NULL COMMENT '分类id',
  `content` longtext COLLATE utf8_esperanto_ci NOT NULL,
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1:显示  2：停止',
  `top` tinyint(1) NOT NULL DEFAULT '2' COMMENT '是否推荐   1:推荐  2：不推荐',
  `orderlist` int(10) NOT NULL DEFAULT '0' COMMENT '排序',
  `time` int(10) NOT NULL,
  `thumbnail` varchar(100) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0' COMMENT '缩略图',
  `abstract` text COLLATE utf8_esperanto_ci NOT NULL COMMENT '摘要',
  `source` varchar(200) COLLATE utf8_esperanto_ci NOT NULL COMMENT '来源',
  `ReadingNumber` int(10) NOT NULL DEFAULT '0' COMMENT '阅读数',
  `member` tinyint(1) NOT NULL DEFAULT '2' COMMENT '会员   1：会员观看  2：所有观看',
  `watermark` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1：添加  2： 不添加   水印',
  PRIMARY KEY (`id`),
  KEY `title` (`title`),
  KEY `classId` (`classId`),
  KEY `status` (`status`),
  KEY `orderlist` (`orderlist`),
  KEY `time` (`time`)
) ENGINE=InnoDB AUTO_INCREMENT=400 DEFAULT CHARSET=utf8 COLLATE=utf8_esperanto_ci;

