-- ----------------------------
-- Table structure for `user`
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '等级管理',
  `name` varchar(50) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '用户名',
  `pwd` char(32) COLLATE utf8_esperanto_ci NOT NULL COMMENT '登陆密码',
  `type` tinyint(1) NOT NULL COMMENT '1:企业   2：供应商  3：普通企业',
  `record` int(2) NOT NULL DEFAULT '0' COMMENT '观看次数',
  `level` int(1) NOT NULL DEFAULT '1' COMMENT '会员等级   2:会员   1：普通会员',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1:正常    2：暂停',
  `time` int(10) NOT NULL DEFAULT '0' COMMENT '注册时间',
  `examine` int(11) NOT NULL DEFAULT '1' COMMENT ' 1：未审核 2：未通过 3：通过  ',
  `corporation` varchar(50) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0' COMMENT '联系人',
  `companyName` varchar(100) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0' COMMENT '公司名称',
  `companyProfile` text COLLATE utf8_esperanto_ci NOT NULL COMMENT '公司简介',
  `contact` varchar(50) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0' COMMENT '联系方式',
  `companyAddress` varchar(200) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0' COMMENT '公司地址',
  `industryClass` int(2) NOT NULL DEFAULT '0' COMMENT '行业分类',
  `license` varchar(100) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0' COMMENT '营业执照图片',
  `tax` varchar(100) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0' COMMENT '税务登记',
  `prove` varchar(100) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0' COMMENT '施工证明',
  `Product` varchar(100) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0' COMMENT '产品检验报告',
  `sessId` varchar(100) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0',
  `logImg` varchar(100) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0',
  `email` varchar(50) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `name` (`name`),
  KEY `pwd` (`pwd`),
  KEY `type` (`type`),
  KEY `record` (`record`),
  KEY `level` (`level`),
  KEY `status` (`status`),
  KEY `time` (`time`),
  KEY `examine` (`examine`),
  KEY `industryClass` (`industryClass`),
  KEY `sessId` (`sessId`)
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8 COLLATE=utf8_esperanto_ci;

