-- ----------------------------
-- Table structure for `video`
-- ----------------------------
DROP TABLE IF EXISTS `video`;
CREATE TABLE `video` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(200) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0',
  `videoUrl` varchar(200) COLLATE utf8_esperanto_ci NOT NULL COMMENT '视频url',
  `classId` int(10) NOT NULL COMMENT '行业Id',
  `orderlist` int(10) NOT NULL DEFAULT '0',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1:启动   2：暂停',
  `coverUrl` varchar(200) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0' COMMENT '封面图片',
  `duration` varchar(40) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0' COMMENT '视频时长',
  `time` int(10) NOT NULL DEFAULT '0',
  `abstract` text COLLATE utf8_esperanto_ci NOT NULL,
  `resourceClass` int(10) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `title` (`title`),
  KEY `classId` (`classId`),
  KEY `orderlist` (`orderlist`),
  KEY `status` (`status`),
  KEY `resourceClass` (`resourceClass`),
  KEY `time` (`time`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 COLLATE=utf8_esperanto_ci;

-- ----------------------------
-- Records of video
