INSERT INTO `video` VALUES ('1', '公安英模赴京观礼回沪　和平年代守护祖国平安', '14416952991000.MP4', '5', '0', '1', '14416060211000.png', '', '1441695299', '<p>		</p><p><br/></p><p><br/></p><p style=\";text-align: justify;font-size: 14px;font-family: Calibri, sans-serif;color: rgb(0, 0, 0);font-style: normal;font-variant: normal;font-weight: normal;letter-spacing: normal;orphans: auto;white-space: normal;widows: auto;word-spacing: 0px;-webkit-text-stroke-width: 0px;background-color: rgb(255, 255, 255);text-indent: 32px;line-height: 21px\"><span style=\"font-size: 16px;font-family: 宋体\"></span></p><p style=\";text-align: justify;font-size: 14px;font-family: Calibri, sans-serif;color: rgb(0, 0, 0);font-style: normal;font-variant: normal;font-weight: normal;letter-spacing: normal;orphans: auto;white-space: normal;widows: auto;word-spacing: 0px;-webkit-text-stroke-width: 0px;background-color: rgb(255, 255, 255);text-indent: 32px;line-height: 21px\"><span style=\"font-size: 16px;font-family: 宋体\">8</span><span style=\"font-size: 16px;font-family: 宋体\">月27日晚上，黄浦消防支队对辖区各大商圈及人员密集场所进行了消防安全夜间突击检查，电视台记者随警出动，对复兴中路上一家游戏厅和一家快捷酒店存在的占用消防通道、消防安全意识薄弱等隐患进行了监督整改。</span></p><p style=\"text-align: justify; font-size: 14px; font-family: Calibri,sans-serif; color: rgb(0, 0, 0); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; white-space: normal; word-spacing: 0px; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 21px;\"><br/></p>', '118');
INSERT INTO `video` VALUES ('2', '消防瞭望塔　曾经的“城市眼睛”', '14416946311000.MP4', '1', '0', '1', '14416061411000.png', '', '1441695266', '<p>		</p><p style=\";text-align: justify;font-size: 14px;font-family: Calibri, sans-serif;color: rgb(0, 0, 0);font-style: normal;font-variant: normal;font-weight: normal;letter-spacing: normal;orphans: auto;white-space: normal;widows: auto;word-spacing: 0px;-webkit-text-stroke-width: 0px;background-color: rgb(255, 255, 255);text-indent: 32px;line-height: 21px\"><span style=\"font-size: 16px;font-family: 宋体\">8</span><span style=\"font-size: 16px;font-family: 宋体\">月27日晚上，黄浦消防支队对辖区各大商圈及人员密集场所进行了消防安全夜间突击检查，电视台记者随警出动，对复兴中路上一家游戏厅和一家快捷酒店存在的占用消防通道、消防安全意识薄弱等隐患进行了监督整改。</span></p><p><span style=\"color: rgb(0, 0, 0);font-family: 宋体;font-size: 16px;font-style: normal;font-variant: normal;font-weight: normal;letter-spacing: normal;line-height: 21.328125px;orphans: auto;text-align: justify;text-indent: 32px;white-space: normal;widows: auto;word-spacing: 0px;-webkit-text-stroke-width: 0px;background-color: rgb(255, 255, 255);display: inline !important;float: none\"></span><br/></p>', '118');
INSERT INTO `video` VALUES ('3', '关于大学生军训期间加强消防安全知识教育的通知', '1442906699.mp4', '5', '0', '1', '1442301268.png', '0', '1442906702', '<p>		</p><p>　　为增强高校学生消防安全意识，全面提高国民消防安全素质和自防自救能力，根据公安部、教育部、民政部、农业部等8部门《关于推进消防安全宣传教育进机关进学校进社区进企业进农村进家庭进网站工作的指导意见》（公消[2015]191号）要求，现就大学生军训期间加强消防安全知识教育作如下通知：</p>', '118');
INSERT INTO `video` VALUES ('4', '关于进一步做好钢结构防火喷涂工程消防验收工作的通知', '1442302545.mp4', '5', '0', '1', '1442302545.jpg', '0', '1442306327', '<p>　　各有关单位：<br/><br/>　　为深化消防行政审批制度改革，简化行政审批手续，结合公安部《社会消防技术服务管理规定》有关要求，现就进一步做好本市钢结构防火喷涂工程消防验收工作的有关事项通知如下：<br/><br/>　　一、即日起本市范围内不再实行钢结构防火喷涂工程施工质量竣工验收检测制度，原《关于在本市范围内实行钢结构防火喷涂工程施工质量竣工验收检测制度的通知》（沪消发〔2008〕123号）废止。<br/><br/>　　二、建设、设计、施工、工程监理等单位应当遵守消防法规、建设工程质量管理法规和国家消防技术标准，对新建、改建、扩建工程中钢结构防火喷涂工程的消防设计、施工质量负责。工程质量自查验收时，相关单位应填写《工程竣工验收报告（消防）—钢结构防火喷涂工程》（见附件）。申报消防验收或办理竣工消防验收备案时，建设单位应将该表填好后作为消防申报资料中“工程竣工验收报告（消防）”的附表一并提交。<br/><br/>　　此通知。</p>', '118');
INSERT INTO `video` VALUES ('5', '上海市社会消防组织管理规定', '1442302750.mp4', '5', '0', '1', '1442302750.jpg', '0', '1442302750', '<p style=\";text-align: justify;font-size: 14px;font-family: Calibri;color: rgb(0, 0, 0);font-style: normal;font-variant: normal;font-weight: normal;letter-spacing: normal;orphans: auto;white-space: normal;widows: auto;word-spacing: 0px;-webkit-text-stroke-width: 0px;background-color: rgb(255, 255, 255);text-indent: 32px;line-height: 21px\"><span style=\"font-size: 16px;font-family: 宋体\">本规定所称社会消防组织，是指除公安消防队以外的其他承担火灾预防、扑救以及应急救援的消防组织，包括专职消防队、志愿消防队和基层消防安全组织。</span></p><p style=\";text-align: justify;font-size: 14px;font-family: Calibri;color: rgb(0, 0, 0);font-style: normal;font-variant: normal;font-weight: normal;letter-spacing: normal;orphans: auto;white-space: normal;widows: auto;word-spacing: 0px;-webkit-text-stroke-width: 0px;background-color: rgb(255, 255, 255);text-indent: 32px;line-height: 21px\"><span style=\"font-size: 16px;font-family: 宋体\">专职消防队包括政府专职消防队和单位专职消防队。政府专职消防队是指各级人民政府组建的主要承担火灾扑救工作的非现役消防组织。单位专职消防队是指由企业、事业单位组建的主要承担本单位火灾扑救工作的消防组织。</span></p><p style=\";text-align: justify;font-size: 14px;font-family: Calibri;color: rgb(0, 0, 0);font-style: normal;font-variant: normal;font-weight: normal;letter-spacing: normal;orphans: auto;white-space: normal;widows: auto;word-spacing: 0px;-webkit-text-stroke-width: 0px;background-color: rgb(255, 255, 255);text-indent: 32px;line-height: 21px\"><span style=\"font-size: 16px;font-family: 宋体\">志愿消防队是指根据消防安全需要，由机关、团体、企业、事业等单位以及居(村)民委员会组建的承担群众性自防自救工作的消防组织。</span></p><p><br/></p>', '118');
INSERT INTO `video` VALUES ('6', '关于转发公安部消防局《关于执行新版消防技术规范有关问题的通知》的通知', '1442302783.mp4', '1', '0', '1', '1442302783.jpg', '0', '1442306309', '<p>　　各相关单位：<br/><br/>　　新版《建筑设计防火规范》（GB 50016-2014）于今年5月1日起实施，现将公安部消防局《关于执行新版消防技术规范有关问题的通知》（公消[2015]98号）予以转发。同时，结合上海实际情况，将有关具体问题明确如下：<br/><br/>　　一、对于5月1日前总体设计文件经公安机关消防机构审核同意的新建、扩建建设工程，若施工图于5月1日后送审图公司审查的，消防设计应执行新版规范。<br/><br/>　　二、新版规范实施后，我市现有工程建设消防规范、消防局技术规定与《建筑设计防火规范》GB 50016-2014强制性条文冲突时，以新版规范为准。<br/><br/>　　此通知。<br/><br/>　　附件：《关于执行新版消防技术规范有关问题的通知》（公消[2015]98号）</p>', '119');
INSERT INTO `video` VALUES ('7', '上海金山消防努力打造新时期和谐警营建设新篇章', '1442303921.mp4', '5', '0', '1', '1442303921.jpg', '0', '1442306271', '<p>　　近日，上海金山消防支队紧密结合部队实际，积极探索新形势下加强部队警营文化新方法和新思路，精心搭建基层警营文化平台，以“四抓四提升”为牵引强力推进和谐警营文化建设，确保部队安全稳定。<br/><br/>　　抓组织体系，提升警营文化建设指导力度。支队党委把制度建设作为推进“班子和谐”的重要举措来抓，把民主集中制建设作为班子建设的重中之重，在部队内部大兴民主之风，努力营造班子成员之间、上下级之间、官兵之间平等团结、健康向上的氛围。</p>', '118');
INSERT INTO `video` VALUES ('8', '关于表彰第二届上海市119消防奖先进集体和个人的决定', '1442303981.mp4', '2', '0', '1', '1442303981.jpg', '0', '1442306235', '<p>　　各区、县人民政府，市消防委各成员单位，各市级工业开发区管委会，各大型国有企业：<br/><br/>　　近年来，本市广大企事业单位和市民群众积极支持、参与消防工作，踊跃投身消防事业，共同维护公共消防安全，涌现出一大批热心消防公益事业的先进集体和个人，为提高全社会防控火灾的能力和水平发挥了重要作用。为鼓舞先进、树立典型，上海市消防安全委员会决定，授予以下24家先进集体和24名先进个人第二届上海市119消防奖。</p>', '118');
INSERT INTO `video` VALUES ('9', '上海虹口区检查人员密集场所消防安全', '1442304014.mp4', '5', '0', '1', '1442304014.jpg', '0', '1442306208', '<p>　　近日，上海虹口区副区长张伟带队检查人员密集场所消防安全工作。区公安、消防、安监、商务等相关部门及街道分管领导陪同检查。<br/><br/>　　张伟副区长一行听取了单位负责人关于消防安全工作情况的汇报，实地查看了消防设施和灭火器材配备、防火分隔和防火间距、各项消防安全管理制度等情况，并就做好下步消防安全工作提出要求：一要部门联动，开展人员密集场所消防安全隐患联合排查整治行动，通过“错时查”、“拉网式”排查等方式，发现并消除一批火灾隐患，全力维护区域的一方平安。二要警钟常鸣，充分落实消防安全主体责任，加大自查自纠力度，自觉规范强化日常消防安全管理，加强对消防设施的维护保养，扎实开展火灾防控工作。三要强化消防宣传，加强社会单位“四个能力”建设，提高单位自主管理、自我防范的能力，并鼓励群众积极举报火灾隐患，营造消防安全群防群治氛围。</p>', '118');
INSERT INTO `video` VALUES ('10', '1111111111', '1442906849.mp4', '5', '1', '1', '1442906773.jpg', '0', '1442906854', '<p>		</p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p>111111111111111<br/></p>', '118');

-- ----------------------------
-- Table structure for `videocomplete`
-- ----------------------------
DROP TABLE IF EXISTS `videocomplete`;
CREATE TABLE `videocomplete` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `videId` int(10) NOT NULL COMMENT '视频id',
  `userId` int(10) NOT NULL,
  `time` int(10) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `videId` (`videId`),
  KEY `userId` (`userId`),
  KEY `userId_2` (`userId`)
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8 COLLATE=utf8_esperanto_ci;

