-- ----------------------------
-- Table structure for `videohistory`
-- ----------------------------
DROP TABLE IF EXISTS `videohistory`;
CREATE TABLE `videohistory` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `videoID` int(10) NOT NULL,
  `userID` int(10) NOT NULL,
  `time` int(10) NOT NULL,
  `recordingSpot` varchar(200) COLLATE utf8_esperanto_ci NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `videoID` (`videoID`),
  KEY `userID` (`userID`),
  KEY `time` (`time`)
) ENGINE=InnoDB AUTO_INCREMENT=129 DEFAULT CHARSET=utf8 COLLATE=utf8_esperanto_ci;

